memberdef.initializer:
if ( s < m_C0.length() ) {           \
  m_C0.FUN( s, __VA_ARGS__ );        \
} else {                             \
  s -= m_C0.length();                \
  if ( s < m_C1.length() ) {         \
    m_C1.FUN( s, __VA_ARGS__ );      \
  } else {                           \
